'use strict';

var titlecase = require( './../lib' );

var str;

str = titlecase( 'beep boop foo bar' );
console.log( str );
// => 'Beep Boop Foo Bar'

str = titlecase( 'Beep' );
console.log( str );
// => 'Beep'

str = titlecase( 'BeEp' );
console.log( str );
// => 'BeEp'

str = titlecase( '$**_beep_BoOp_**$' );
console.log( str );
// => '$**_beep_BoOp_**$'

str = titlecase( '' );
console.log( str );
// => ''
