'use strict';

var utf16ToUTF8Array = require( './../lib' );

var values;
var out;
var i;

values = [
	'Ladies + Gentlemen',
	'An encoded string!',
	'Dogs, Cats & Mice',
	'☃',
	'æ'
];
for ( i = 0; i < values.length; i++ ) {
	out = utf16ToUTF8Array( values[ i ] );
	console.log( '%s: %s', values[ i ], out.join( ',' ) );
}
