'use strict';

// MODULES //

var tape = require( 'tape' );
var HOURS_IN_DAY = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof HOURS_IN_DAY, 'number', 'main export is a number' );
	t.end();
});

tape( 'the value equals 24', function test( t ) {
	t.strictEqual( HOURS_IN_DAY, 24, 'equals 24' );
	t.end();
});
