'use strict';

/**
* Number of hours in a week.
*
* @module @stdlib/time/constants/hours-in-week
* @type {integer32}
*
* @example
* var HOURS_IN_WEEK = require( '@stdlib/time/constants/hours-in-week' );
* // returns 168
*/


// MAIN //

/**
* The number of hours in a week is given by
*
* ``` tex
* 7 \cdot 24 = 168
* ```
*
* @constant
* @type {integer32}
* @default 168
*/
var HOURS_IN_WEEK = 168|0; // asm type annotation


// EXPORTS //

module.exports = HOURS_IN_WEEK;
