'use strict';

// MODULES //

var tape = require( 'tape' );
var HOURS_IN_WEEK = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof HOURS_IN_WEEK, 'number', 'main export is a number' );
	t.end();
});

tape( 'the value equals 7*24=168', function test( t ) {
	t.strictEqual( HOURS_IN_WEEK, 7*24, 'equals 7*24=168' );
	t.end();
});
