'use strict';

/**
* Number of milliseconds in a day.
*
* @module @stdlib/time/constants/milliseconds-in-day
* @type {integer32}
*
* @example
* var MILLISECONDS_IN_DAY = require( '@stdlib/time/constants/milliseconds-in-day' );
* // returns 86400000
*/


// MAIN //

/**
* The number of milliseconds in a day is given by
*
* ``` tex
* 1000 \cdot 60 \cdot 60 \cdot 24 = 86400000
* ```
*
* @constant
* @type {integer32}
* @default 86400000
*/
var MILLISECONDS_IN_DAY = 86400000|0; // asm type annotation


// EXPORTS //

module.exports = MILLISECONDS_IN_DAY;
