'use strict';

/**
* Number of milliseconds in an hour.
*
* @module @stdlib/time/constants/milliseconds-in-hour
* @type {integer32}
*
* @example
* var MILLISECONDS_IN_HOUR = require( '@stdlib/time/constants/milliseconds-in-hour' );
* // returns 3600000
*/


// MAIN //

/**
* The number of milliseconds in an hour is given by
*
* ``` tex
* 1000 \cdot 60 \cdot 60 = 3600000
* ```
*
* @constant
* @type {integer32}
* @default 3600000
*/
var MILLISECONDS_IN_HOUR = 3600000|0; // asm type annotation


// EXPORTS //

module.exports = MILLISECONDS_IN_HOUR;
