'use strict';

// MODULES //

var tape = require( 'tape' );
var MILLISECONDS_IN_MINUTE = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof MILLISECONDS_IN_MINUTE, 'number', 'main export is a number' );
	t.end();
});

tape( 'the value equals 1000*60=60000', function test( t ) {
	t.strictEqual( MILLISECONDS_IN_MINUTE, 1000*60, 'equals 1000*60=60000' );
	t.end();
});
