'use strict';

// MODULES //

var tape = require( 'tape' );
var MINUTES_IN_DAY = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof MINUTES_IN_DAY, 'number', 'main export is a number' );
	t.end();
});

tape( 'the value equals 60*24=1440', function test( t ) {
	t.strictEqual( MINUTES_IN_DAY, 60*24, 'equals 60*24=1440' );
	t.end();
});
