'use strict';

/**
* Number of months in a year.
*
* @module @stdlib/time/constants/months-in-year
* @type {integer32}
*
* @example
* var MONTHS_IN_YEAR = require( '@stdlib/time/constants/months-in-year' );
* // returns 12
*/


// MAIN //

/**
* The number of months in a year.
*
* @constant
* @type {integer32}
* @default 12
*/
var MONTHS_IN_YEAR = 12|0; // asm type annotation


// EXPORTS //

module.exports = MONTHS_IN_YEAR;
