'use strict';

// MODULES //

var tape = require( 'tape' );
var MONTHS_IN_YEAR = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof MONTHS_IN_YEAR, 'number', 'main export is a number' );
	t.end();
});

tape( 'the value equals 12', function test( t ) {
	t.strictEqual( MONTHS_IN_YEAR, 12, 'equals 12' );
	t.end();
});
