/* tslint:disable:no-unused-expression */

import time = require( './index' );


// TESTS //

// The exported object has the expected function interfaces...
{
	time.constants; // $ExpectType TIME
	time.dayOfQuarter; // $ExpectType (month?: string | number | Date | undefined, day?: number | undefined, year?: number | undefined) => number
	time.dayOfYear; // $ExpectType (month?: string | number | Date | undefined, day?: number | undefined, year?: number | undefined) => number
	time.daysInMonth; // $ExpectType (month?: string | number | Date | undefined, year?: number | undefined) => number
	time.daysInYear; // $ExpectType (value?: number | Date | undefined) => number
	time.hoursInMonth; // $ExpectType (month?: string | number | Date | undefined, year?: number | undefined) => number
	time.hoursInYear; // $ExpectType (value?: number | Date | undefined) => number
	time.isoWeeksInYear; // $ExpectType (value?: number | Date | undefined) => number
	time.minutesInMonth; // $ExpectType (month?: string | number | Date | undefined, year?: number | undefined) => number
	time.minutesInYear; // $ExpectType (value?: number | Date | undefined) => number
	time.now; // $ExpectType () => number
	time.quarterOfYear; // $ExpectType (month?: string | number | Date | undefined) => number
	time.secondsInMonth; // $ExpectType (month?: string | number | Date | undefined, year?: number | undefined) => number
	time.secondsInYear; // $ExpectType (value?: number | Date | undefined) => number
	time.tic; // $ExpectType () => number[]
	time.toc; // $ExpectType (time: number[]) => number[]
}
