'use strict';

/**
* Time in seconds since the epoch.
*
* @module @stdlib/time/now
*
* @example
* var now = require( '@stdlib/time/now' );
*
* var ts = now();
* // returns <number>
*/

// MODULES //

var bool = require( './detect.js' );
var main = require( './now.js' );
var polyfill = require( './polyfill.js' );


// MAIN //

var now;
if ( bool ) {
	now = main;
} else {
	now = polyfill;
}


// EXPORTS //

module.exports = now;
