'use strict';

var quarterOfYear = require( './../lib' );

var months;
var q;
var i;

months = [
	'January',
	'February',
	'March',
	'April',
	'May',
	'June',
	'July',
	'August',
	'September',
	'October',
	'November',
	'December'
];

for ( i = 0; i < months.length; i++ ) {
	q = quarterOfYear( months[ i ] );
	console.log( 'The month of %s is in Q%d.', months[ i ], q );
}
