'use strict';

/**
* Command-line interface (CLI).
*
* @module @stdlib/tools/cli
*
* @example
* var CLI = require( '@stdlib/tools/cli' );
*
* var opts = {
*     'pkg': require( './path/to/package.json' ),
*     'help': 'Usage: beep [options] <boop>',
*     'title': 'foo',
*     'updates': true,
*     'options': {
*         'boolean': [
*             'help',
*             'version'
*         ]
*     }
* };
* var cli = new CLI( opts );
* // returns <CLI>
*
* cli.exit();
*/

// MODULES //

var CLI = require( './cli.js' );


// EXPORTS //

module.exports = CLI;
