'use strict';

// MODULES //

var tape = require( 'tape' );
var noop = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( typeof noop === 'function', 'main export is a function' );
	t.end();
});

tape( 'function does not take any arguments', function test( t ) {
	t.ok( noop.length === 0, 'number of parameters is equal to zero' );
	t.end();
});

tape( 'function returns `undefined`', function test( t ) {
	t.ok( noop() === void 0, 'returns undefined' );
	t.end();
});
