'use strict';

// MODULES //

var tape = require( 'tape' );
var noop = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof noop, 'function', 'main export is a function' );
	t.end();
});

tape( 'function does not take any arguments', function test( t ) {
	t.equal( noop.length, 0, 'number of parameters is equal to zero' );
	t.end();
});

tape( 'function returns `undefined`', function test( t ) {
	t.equal( noop(), void 0, 'returns undefined' );
	t.end();
});

tape( 'function should be empty', function test( t ) {
	t.equal( noop.toString(), 'function noop(){}', 'is empty' );
	t.end();
});
