'use strict';

var httpServer = require( './../lib' );

var opts = {
	'dir': './',
	'port': 7331,
	'hostname': 'localhost',
	'open': false
};

httpServer( opts, clbk );

function clbk( error, server ) {
	if ( error ) {
		throw error;
	}
	// Give the user a few seconds to open her web browser before closing the server...
	setTimeout( onTimeout, 5000 );

	function onTimeout() {
		server.close();
	}
}
