'use strict';

/**
* Create a simple HTTP server.
*
* @module @stdlib/tools/simple-http-server
*
* @example
* var httpServer = require( '@stdlib/tools/simple-http-server' );
*
* var opts = {
*     'html': '<h1>Beep</h1>'
* };
* httpServer( opts, onReady );
*
* function onReady( error, server ) {
*     if ( error ) {
*         throw error;
*     }
*     server.close();
* }
*
* @example
* var httpServer = require( '@stdlib/tools/simple-http-server' );
*
* var opts = {
*     'dir': './',
*     'port': 7331,
*     'hostname': 'localhost',
*     'open': false
* };
* httpServer( opts, onReady );
*
* function onReady( error, server ) {
*     if ( error ) {
*         throw error;
*     }
*     server.close();
* }
*/

// MODULES //

var httpServer = require( './server.js' );


// EXPORTS //

module.exports = httpServer;
