'use strict';

/**
* Extracts HTTP server options from input function options.
*
* @private
* @param {Object} options - function options
* @param {number} [options.port] - server port
* @param {number} [options.maxport] - max server port
* @param {string} [options.hostname] - server hostname
* @param {string} [options.address] - server address
* @returns {Object} server options
*
* @example
* var options = {
*     'html': '<h1>beep</h1>',
*     'open': true,
*     'port': 7331,
*     'address': '127.0.0.1'
* };
* var out = opts( options );
* // returns {'port': 7331, 'address': '127.0.0.1'}
*/
function opts( options ) {
	var out = {};
	if ( options.hasOwnProperty( 'port' ) ) {
		out.port = options.port;
	}
	if ( options.hasOwnProperty( 'maxport' ) ) {
		out.maxport = options.maxport;
	}
	if ( options.hasOwnProperty( 'hostname' ) ) {
		out.hostname = options.hostname;
	}
	if ( options.hasOwnProperty( 'address' ) ) {
		out.address = options.address;
	}
	return out;
} // end FUNCTION opts()


// EXPORTS //

module.exports = opts;
