'use strict';

// MODULES //

var tape = require( 'tape' );
var validateArray = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof validateArray, 'function', 'main export is a function' );
	t.end();
});

tape( 'exports a factory method to create array validation functions', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof validateArray.create, 'function', 'create property is a function' );
	t.end();
});

tape( 'exports a validate function which provides fewer guarantees when validating input arguments', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof validateArray.raw, 'function', 'raw property is a function' );
	t.end();
});
