'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var hasSharedArrayBufferSupport = require( '@stdlib/utils/detect-sharedarraybuffer-support' );
var isSharedArrayBuffer = require( '@stdlib/assert/is-sharedarraybuffer' );
var pkg = require( './../package.json' ).name;
var ctor = require( './../lib' );


// VARIABLES //

var opts = {
	'skip': !hasSharedArrayBufferSupport()
};


// MAIN //

bench( pkg, opts, function benchmark( b ) {
	var buf;
	var i;
	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		buf = new ctor( 0 );
		if ( buf.byteLength !== 0 ) {
			b.fail( 'should have length 0' );
		}
	}
	b.toc();
	if ( !isSharedArrayBuffer( buf ) ) {
		b.fail( 'should return a SharedArrayBuffer' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
