'use strict';

var indexOf = require( '@stdlib/utils/index-of' );
var dtypes = require( './../lib' );

var DTYPES = dtypes();
var bool;

function isdtype( str ) {
	if ( indexOf( DTYPES, str ) === -1 ) {
		return false;
	}
	return true;
}

bool = isdtype( 'float64' );
console.log( bool );
// => true

bool = isdtype( 'int8' );
console.log( bool );
// => true

bool = isdtype( 'uint16' );
console.log( bool );
// => true

bool = isdtype( 'beep' );
console.log( bool );
// => false
