'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var hasNodeBufferSupport = require( '@stdlib/utils/detect-node-buffer-support' )();
var polyfill = require( './../lib/polyfill.js' );
var ctor = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ctor, 'function', 'main export is a function' );
	t.end();
});

tape( 'if an environment supports `Buffer`, the export is an alias for `Buffer`', function test( t ) {
	var Foo;

	Foo = proxyquire( './../lib', {
		'@stdlib/utils/detect-node-buffer-support': isTrue,
		'./buffer.js': Mock
	});
	t.strictEqual( Foo, Mock, 'returns builtin' );

	if ( hasNodeBufferSupport ) {
		t.strictEqual( ctor, Buffer, 'is alias' ); // eslint-disable-line stdlib/require-buffer
	}

	t.end();

	function Mock() {
		return this;
	}

	function isTrue() {
		return true;
	}
});

tape( 'if an environment does not support `Buffer`, the export is a polyfill', function test( t ) {
	var Foo;

	Foo = proxyquire( './../lib', {
		'@stdlib/utils/detect-node-buffer-support': isFalse
	});

	t.strictEqual( Foo, polyfill, 'returns polyfill' );
	t.end();

	function isFalse() {
		return false;
	}
});
