'use strict';

// MODULES //

var isArrayLikeObject = require( '@stdlib/assert/is-array-like-object' );
var Buffer = require( '@stdlib/types/buffer/ctor' );


// MAIN //

/**
* Allocates a buffer using an octet array.
*
* @param {ArrayLikeObject} arr - octet array
* @throws {TypeError} must provide an array-like object
* @returns {Buffer} new `Buffer` instance
*
* @example
* var fromArray = require( '@stdlib/type/buffer/from-array' );
*
* var buf = fromArray( [ 1, 2, 3, 4 ] );
* // returns <Buffer>
*/
function fromArray( arr ) {
	if ( !isArrayLikeObject( arr ) ) {
		throw new TypeError( 'invalid input argument. Must provide an array-like object. Value: `' + arr + '`' );
	}
	return new Buffer( arr ); // eslint-disable-line no-buffer-constructor
} // end FUNCTION fromArray()


// EXPORTS //

module.exports = fromArray;
