'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var isBuffer = require( '@stdlib/assert/is-buffer' );
var ArrayBuffer = require( '@stdlib/types/array/buffer' );
var Uint8Array = require( '@stdlib/types/array/uint8' );
var pkg = require( './../package.json' ).name;
var fromArrayBuffer = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var view;
	var buf;
	var out;
	var i;

	buf = new ArrayBuffer( 10 );
	view = new Uint8Array( buf );

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		view[ 0 ] = i % 256;
		out = fromArrayBuffer( buf );
		if ( out.length !== buf.byteLength ) {
			b.fail( 'should have expected length' );
		}
	}
	b.toc();
	if ( !isBuffer( out ) ) {
		b.fail( 'should return a Buffer' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

