'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var Uint8Array = require( '@stdlib/types/array/uint8' );
var ArrayBuffer = require( '@stdlib/types/array/buffer' );
var isBuffer = require( '@stdlib/assert/is-buffer' );
var hasFrom = require( './../lib/has_from.js' );
var NODE_VERSION = require( './../lib/node_version.js' );
var fromArrayBuffer = require( './../lib/main.js' );


// VARIABLES //

var opts = {
	'skip': !hasFrom
};


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof fromArrayBuffer, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if not provided an ArrayBuffer', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		{},
		[],
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[ i ] ), TypeError, 'throws an error when provided ' + values[ i ] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			fromArrayBuffer( value );
		};
	}
});

