'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var isBuffer = require( '@stdlib/assert/is-buffer' );
var isFunction = require( '@stdlib/assert/is-function' );
var Buffer = require( '@stdlib/types/buffer/ctor' );
var pkg = require( './../package.json' ).name;
var fromBuffer = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var buf;
	var out;
	var i;

	if ( isFunction( Buffer.alloc ) ) {
		buf = Buffer.alloc( 10 );
	} else {
		buf = new Buffer( 10 ); // eslint-disable-line no-buffer-constructor
	}

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		buf[ 0 ] = i % 20;
		out = fromBuffer( buf );
		if ( out.length !== 10 ) {
			b.fail( 'should have length 10' );
		}
	}
	b.toc();
	if ( !isBuffer( out ) ) {
		b.fail( 'should return a Buffer' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
