'use strict';

var Buffer = require( '@stdlib/types/buffer/ctor' );
var isFunction = require( '@stdlib/assert/is-function' );
var fromBuffer = require( './../lib' );

var bool;
var b1;
var b2;
var i;

// Allocate a new buffer...
if ( isFunction( Buffer.alloc ) ) {
	b1 = Buffer.alloc( 10 );
} else {
	b1 = new Buffer( 10 ); // eslint-disable-line no-buffer-constructor
}

// Generate a new buffer from the existing buffer:
b2 = fromBuffer( b1 );

bool = ( b2 === b1 );
console.log( 'Same reference: %s', bool );
// => Same reference: false

bool = ( b2.length === b1.length );
console.log( 'Same length: %s', bool );
// => Same reference: true

for ( i = 0; i < b2.length; i++ ) {
	console.log( b2[ i ] === b1[ i ] );
	// => true
}
