'use strict';

/**
* Revive a JSON-serialized `Buffer`.
*
* @module @stdlib/types/buffer/reviver
*
* @example
* var parseJSON = require( '@stdlib/utils/parse-json' );
* var reviver = require( '@stdlib/types/buffer/reviver' );
*
* var str = '{"type":"Buffer","data":[5,3]}';
*
* var buf = parseJSON( str, reviver );
* // returns <Buffer>[ 5, 3 ]
*/

// MODULES //

var reviver = require( './main.js' );


// EXPORTS //

module.exports = reviver;
