'use strict';

var allocUnsafe = require( '@stdlib/types/buffer/alloc-unsafe' );
var randint = require( '@stdlib/math/base/random/discrete-uniform' );
var toJSON = require( './../lib' );

var buf;
var i;

buf = allocUnsafe( 100 );
for ( i = 0; i < buf.length; i++ ) {
	buf[ i ] = randint( 0, 255 );
}

console.log( toJSON( buf ) );
