'use strict';

/**
* Return a JSON representation of a `Buffer`.
*
* @module @stdlib/types/buffer/to-json
*
* @example
* var array2buffer = require( '@stdlib/types/buffer/from-array' );
* var toJSON = require( '@stdlib/types/buffer/to-json' );
*
* var buf = array2buffer( [ 1, 2 ] );
* // returns <Buffer>
*
* var json = toJSON( buf );
* // returns { 'type': 'Buffer', 'data': [ 1, 2 ] }
*/

// MODULES //

var toJSON = require( './main.js' );


// EXPORTS //

module.exports = toJSON;
