'use strict';

/**
* Returns the complex conjugate of a complex number.
*
* @param {Complex} z - complex number
* @returns {Complex} complex conjugate
*
* @example
* var Complex128 = require( '@stdlib/types/complex/complex128' );
*
* var z = new Complex128( 5.0, 3.0 );
*
* var v = conj( z );
* // returns <Complex128>
*/
function conj( z ) {
	return new z.constructor( z.re, -z.im );
} // end FUNCTION conj()


// EXPORTS //

module.exports = conj;
