'use strict';

/**
* Return the real and imaginary components of a complex number.
*
* @module @stdlib/types/complex/utils/reim
*
* @example
* var Complex128 = require( '@stdlib/types/complex/float64' );
* var reim = require( '@stdlib/types/complex/utils/reim' );
*
* var z = new Complex128( 5.0, 3.0 );
*
* var out = reim( z );
* // returns <Float64Array>[ 5.0, 3.0 ]
*
* @example
* var Complex64 = require( '@stdlib/types/complex/float32' );
* var reim = require( '@stdlib/types/complex/utils/reim' );
*
* var z = new Complex64( 5.0, 3.0 );
*
* var out = reim( z );
* // returns <Float32Array>[ 5.0, 3.0 ]
*/

// MODULES //

var reim = require( './reim.js' );


// EXPORTS //

module.exports = reim;
