/* eslint-disable no-redeclare */
'use strict';

/**
* Serializes a complex number as a string.
*
* @private
* @returns {String} serialized complex number
*/
function toString() {
	/* eslint-disable no-invalid-this */
	var str = '' + this.real;
	if ( this.imag < 0 ) {
		str += ' - ' + (-this.imag);
	} else {
		str += ' + ' + this.imag;
	}
	str += 'i';
	return str;
} // end FUNCTION toString()


// EXPORTS //

module.exports = toString;
