'use strict';

// MODULES //

var isSingleSegmentCompatible = require( '@stdlib/types/ndarray/base/assert/is-single-segment-compatible' );
var iterationOrder = require( '@stdlib/types/ndarray/base/iteration-order' );
var isRowMajor = require( '@stdlib/types/ndarray/base/assert/is-row-major' );


// MAIN //

/**
* Returns a boolean indicating if an array is row-major contiguous.
*
* @param {NonNegativeIntegerArray} shape - array shape
* @param {IntegerArray} strides - stride array
* @param {NonNegativeInteger} offset - index offset
* @returns {boolean} boolean indicating if an array is row-major contiguous
*
* @example
* var shape = [ 2, 2 ];
* var strides = [ 2, 1 ];
* var offset = 0;
*
* var bool = isRowMajorContiguous( shape, strides, offset );
* // returns true
*
* @example
* var shape = [ 2, 2 ];
* var strides = [ -2, 1 ];
* var offset = 2;
*
* var bool = isRowMajorContiguous( shape, strides, offset );
* // returns false
*
* @example
* var shape = [ 2, 2 ];
* var strides = [ 2, 2 ];
* var offset = 0;
*
* var bool = isRowMajorContiguous( shape, strides, offset );
* // returns false
*/
function isRowMajorContiguous( shape, strides, offset ) {
	return (
		iterationOrder( strides ) !== 0 &&
		isRowMajor( strides ) &&
		isSingleSegmentCompatible( shape, strides, offset )
	);
} // end FUNCTION isRowMajorContiguous()


// EXPORTS //

module.exports = isRowMajorContiguous;
