'use strict';

// MODULES //

var numel = require( '@stdlib/types/ndarray/base/numel' );


// MAIN //

/**
* Returns a boolean indicating if a buffer length is compatible with a provided shape array.
*
* @param {NonNegativeInteger} len - buffer length
* @param {NonNegativeIntegerArray} shape - array shape
* @returns {boolean} boolean indicating if a buffer length is compatible with a provided shape array
*
* @example
* var shape = [ 2, 2 ];
*
* var bool = isShapeBufferCompatible( 4, shape );
* // returns true
*
* @example
* var shape = [ 2, 2 ];
*
* var bool = isShapeBufferCompatible( 3, shape );
* // returns false
*/
function isShapeBufferCompatible( len, shape ) {
	return ( len >= numel( shape ) );
} // end FUNCTION isShapeBufferCompatible()


// EXPORTS //

module.exports = isShapeBufferCompatible;
