'use strict';

// MODULES //

var tape = require( 'tape' );
var isShapeBufferCompatible = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isShapeBufferCompatible, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if a buffer length is compatible with a provided shape array', function test( t ) {
	var shape;
	var bool;

	shape = [ 3, 2 ];
	bool = isShapeBufferCompatible( 10, shape );
	t.strictEqual( bool, true, 'returns expected value' );

	bool = isShapeBufferCompatible( 6, shape );
	t.strictEqual( bool, true, 'returns expected value' );

	shape = [ 1, 1, 1, 2 ];
	bool = isShapeBufferCompatible( 2, shape );
	t.strictEqual( bool, true, 'returns expected value' );

	shape = [ 2, 3, 10 ];
	bool = isShapeBufferCompatible( 60, shape );
	t.strictEqual( bool, true, 'returns expected value' );

	bool = isShapeBufferCompatible( 100, shape );
	t.strictEqual( bool, true, 'returns expected value' );

	t.end();
});

tape( 'the function returns `false` if a buffer length is incompatible with a provided shape array', function test( t ) {
	var shape;
	var bool;

	shape = [ 1, 1, 1, 2 ];
	bool = isShapeBufferCompatible( 1, shape );
	t.strictEqual( bool, false, 'returns expected value' );

	shape = [ 10 ];
	bool = isShapeBufferCompatible( 9, shape );
	t.strictEqual( bool, false, 'returns expected value' );

	shape = [ 2, 2 ];
	bool = isShapeBufferCompatible( 3, shape );
	t.strictEqual( bool, false, 'returns expected value' );

	t.end();
});
