'use strict';

var discreteUniform = require( '@stdlib/math/base/random/discrete-uniform' );
var clampIndex = require( './../lib' );

var idx;
var out;
var i;

for ( i = 0; i < 100; i++ ) {
	idx = discreteUniform( -20, 20 );
	out = clampIndex( idx, 10 );
	console.log( '%d => [%d,%d] => %d', idx, 0, 10, out );
}
