'use strict';

// MAIN //

/**
* Returns an array element.
*
* @private
* @param {...integer} idx - indices
* @returns {*} array element
*/
function get() {
	/* eslint-disable no-invalid-this */
	var idx;
	var i;

	idx = this._offset;
	for ( i = 0; i < arguments.length; i++ ) {
		idx += this._strides[ i ] * arguments[ i ];
	}
	return this._buffer[ idx ];
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
