'use strict';

// MODULES //

var abs = require( '@stdlib/math/base/special/abs' );


// MAIN //

/**
* Returns a boolean indicating if an array is column-major based on a provided stride array.
*
* @param {IntegerArray} strides - stride array
* @returns {boolean} boolean indicating if an array is column-major
*
* @example
* var bool = isColumnMajor( [ 1, 2 ] );
* // returns true
*
* bool = isColumnMajor( [ 2, 1 ] );
* // returns false
*/
function isColumnMajor( strides ) {
	var ndims;
	var s1;
	var s2;
	var i;

	ndims = strides.length;
	if ( ndims === 0 ) {
		return false;
	}
	s1 = abs( strides[ 0 ] );
	for ( i = 1; i < ndims; i++ ) {
		s2 = abs( strides[ i ] );
		if ( s2 < s1 ) {
			return false;
		}
		s1 = s2;
	}
	return true;
} // end FUNCTION isColumnMajor()


// EXPORTS //

module.exports = isColumnMajor;
