'use strict';

/**
* Return a boolean indicating if an array is compatible with a single memory segment.
*
* @module @stdlib/types/ndarray/base/is-single-segment
*
* @example
* var isSingleSegment = require( '@stdlib/types/ndarray/base/is-single-segment' );
*
* var shape = [ 2, 2 ];
* var strides = [ 2, 1 ];
* var offset = 0;
*
* var bool = isSingleSegment( shape, strides, offset );
* // returns true
*
* @example
* var isSingleSegment = require( '@stdlib/types/ndarray/base/is-single-segment' );
*
* var shape = [ 10 ];
* var strides = [ 3 ];
* var offset = 0;
*
* var bool = isSingleSegment( shape, strides, offset );
* // returns false
*/

// MODULES //

var isSingleSegment = require( './main.js' );


// EXPORTS //

module.exports = isSingleSegment;
