'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var randu = require( '@stdlib/math/base/random/randu' );
var discreteUniform = require( '@stdlib/math/base/random/discrete-uniform' );
var isNonNegativeIntegerArray = require( '@stdlib/assert/is-nonnegative-integer-array' ).primitives;
var shape2strides = require( '@stdlib/types/ndarray/base/shape2strides' );
var pkg = require( './../package.json' ).name;
var minmaxViewBufferIndex = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var strides;
	var shape;
	var out;
	var i;

	shape = new Array( 3 );
	shape[ 0 ] = discreteUniform( 0, 10 );
	shape[ 1 ] = discreteUniform( 0, 10 );
	shape[ 2 ] = discreteUniform( 0, 10 );

	strides = shape2strides( shape, 'row-major' );

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		strides[ 2 ] *= ( randu() < 0.5 ) ? -1 : 1;
		out = minmaxViewBufferIndex( shape, strides, 1000 );
		if ( out.length !== 2 ) {
			b.fail( 'should return an array having two elements' );
		}
	}
	b.toc();
	if ( !isNonNegativeIntegerArray( out ) ) {
		b.fail( 'should return an array containing nonnegative integers' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::memory_reuse', function benchmark( b ) {
	var strides;
	var shape;
	var out;
	var mm;
	var i;

	shape = new Array( 3 );
	shape[ 0 ] = discreteUniform( 0, 10 );
	shape[ 1 ] = discreteUniform( 0, 10 );
	shape[ 2 ] = discreteUniform( 0, 10 );

	strides = shape2strides( shape, 'row-major' );

	out = new Array( 2 );

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		strides[ 2 ] *= ( randu() < 0.5 ) ? -1 : 1;
		mm = minmaxViewBufferIndex( out, shape, strides, 1000 );
		if ( mm.length !== 2 ) {
			b.fail( 'should return an array having two elements' );
		}
	}
	b.toc();
	if ( !isNonNegativeIntegerArray( mm ) ) {
		b.fail( 'should return an array containing nonnegative integers' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
