'use strict';

var indexOf = require( '@stdlib/utils/index-of' );
var modes = require( './../lib' );

var MODES = modes();
var bool;

function isMode( str ) {
	if ( indexOf( MODES, str ) === -1 ) {
		return false;
	}
	return true;
}

bool = isMode( 'throw' );
console.log( bool );
// => true

bool = isMode( 'clamp' );
console.log( bool );
// => true

bool = isMode( 'wrap' );
console.log( bool );
// => true

bool = isMode( 'beep' );
console.log( bool );
// => false
