'use strict';

// MODULES //

var MODES = require( './modes.json' );


// MAIN //

/**
* Returns a list of ndarray index modes.
*
* @returns {StringArray} list of ndarray index modes
*
* @example
* var list = modes();
* // returns [ 'throw', 'clamp', 'wrap' ]
*/
function modes() {
	return MODES.slice();
} // end FUNCTION modes()


// EXPORTS //

module.exports = modes;
