'use strict';

// MAIN //

/**
* Returns the number of elements in an array.
*
* @param {NonNegativeIntegerArray} shape - array shape
* @returns {uinteger} number of elements
*
* @example
* var n = numel( [ 3, 3, 3 ] );
* // returns 27
*/
function numel( shape ) {
	var ndims;
	var n;
	var i;

	ndims = shape.length;
	n = 1;
	for ( i = 0; i < ndims; i++ ) {
		n *= shape[ i ];
	}
	return n;
} // end FUNCTION numel()


// EXPORTS //

module.exports = numel;
