'use strict';

var indexOf = require( '@stdlib/utils/index-of' );
var orders = require( './../lib' );

var ORDERS = orders();
var bool;

function isOrder( str ) {
	if ( indexOf( ORDERS, str ) === -1 ) {
		return false;
	}
	return true;
}

bool = isOrder( 'row-major' );
console.log( bool );
// => true

bool = isOrder( 'column-major' );
console.log( bool );
// => true

bool = isOrder( 'beep' );
console.log( bool );
// => false
