'use strict';

// MODULES //

var tape = require( 'tape' );
var strides2order = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof strides2order, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function determines the array order based on a provided stride array', function test( t ) {
	var strides;
	var o;

	// 1-d, both row-major and column-major
	strides = [ 1 ];
	o = strides2order( strides );
	t.strictEqual( o, 'both', 'returns expected value' );

	strides = [ 10 ];
	o = strides2order( strides );
	t.strictEqual( o, 'both', 'returns expected value' );

	strides = [ -1 ];
	o = strides2order( strides );
	t.strictEqual( o, 'both', 'returns expected value' );

	strides = [ -10 ];
	o = strides2order( strides );
	t.strictEqual( o, 'both', 'returns expected value' );

	// 2-d, row-major...
	strides = [ 2, 1 ];
	o = strides2order( strides );
	t.strictEqual( o, 'row-major', 'returns expected value' );

	strides = [ -2, 1 ];
	o = strides2order( strides );
	t.strictEqual( o, 'row-major', 'returns expected value' );

	strides = [ 2, -1 ];
	o = strides2order( strides );
	t.strictEqual( o, 'row-major', 'returns expected value' );

	strides = [ -2, -1 ];
	o = strides2order( strides );
	t.strictEqual( o, 'row-major', 'returns expected value' );

	// 2-d, column-major...
	strides = [ 1, 3 ];
	o = strides2order( strides );
	t.strictEqual( o, 'column-major', 'returns expected value' );

	strides = [ -1, 3 ];
	o = strides2order( strides );
	t.strictEqual( o, 'column-major', 'returns expected value' );

	strides = [ 1, -3 ];
	o = strides2order( strides );
	t.strictEqual( o, 'column-major', 'returns expected value' );

	strides = [ -1, -3 ];
	o = strides2order( strides );
	t.strictEqual( o, 'column-major', 'returns expected value' );

	// 3-d, row-major...
	strides = [ 30, 10, 1 ];
	o = strides2order( strides );
	t.strictEqual( o, 'row-major', 'returns expected value' );

	strides = [ 30, -10, 1 ];
	o = strides2order( strides );
	t.strictEqual( o, 'row-major', 'returns expected value' );

	// 3-d, column-major...
	strides = [ 1, 10, 30 ];
	o = strides2order( strides );
	t.strictEqual( o, 'column-major', 'returns expected value' );

	strides = [ 1, -10, 30 ];
	o = strides2order( strides );
	t.strictEqual( o, 'column-major', 'returns expected value' );

	// 3-d, neither row-major nor column-major...
	strides = [ 30, 1, 10 ];
	o = strides2order( strides );
	t.strictEqual( o, 'none', 'returns expected value' );

	strides = [ 2, 3, 1 ];
	o = strides2order( strides );
	t.strictEqual( o, 'none', 'returns expected value' );

	t.end();
});

tape( 'if provided an empty stride array, the function returns `none`', function test( t ) {
	var o = strides2order( [] );
	t.strictEqual( o, 'none', 'returns expected value' );
	t.end();
});
