'use strict';

// MODULES //

var isInteger = require( '@stdlib/assert/is-integer' ).isPrimitive;


// FUNCTIONS //

/**
* Returns an array element.
*
* @private
* @param {integer} i - index
* @throws {TypeError} index must be an integer value
* @returns {*} array element
*/
function get( i ) {
	/* eslint-disable no-invalid-this */
	if ( !isInteger( i ) ) {
		throw new TypeError( 'invalid input argument. Index must be an integer value. Value: `'+i+'`.' );
	}
	// TODO: support index modes

	return this._buffer[ this._offset + ( this._strides[0]*i ) ];
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
