'use strict';

/**
* Convert a linear index to an array of subscripts.
*
* @module @stdlib/types/ndarray/ind2sub
*
* @example
* var ind2sub = require( '@stdlib/types/ndarray/ind2sub' );
*
* var s = ind2sub( [ 3, 3, 3 ], 17 )
* // returns [ 1, 2, 2 ]
*
* @example
* var ind2sub = require( '@stdlib/types/ndarray/ind2sub' );
*
* var shape = [ 3, 3, 3 ];
* var out = new Array( shape.length );
*
* var s = ind2sub( out, shape, 17 )
* // returns [ 1, 2, 2 ]
*
* var bool = ( s === out );
* // returns true
*/

// MODULES //

var ind2sub = require( './main.js' );


// EXPORTS //

module.exports = ind2sub;
